import axios from 'axios';
import { useState, useEffect } from 'react';
import moment from 'moment'; // Làm việc với ngày giờ

const useFetch = (url) => {
    const [data, setData] = useState([]);
    const [loadedData, setLoadedData] = useState(true);
    const [isloadData, setIsLoadedData] = useState(false);
    // eslint-disable-next-line react-hooks/exhaustive-deps

    useEffect(() => {
        const ourRequest = axios.CancelToken.source();
        let isMounted = true; // Thêm biến để theo dõi component còn tồn tại hay không

        try {
            async function fetchData() {
                let res = await axios.get(url, {
                    cancelToken: ourRequest.token,
                });
                let data = res && res.data ? res.data : [];

                if (data && data.length > 0 && isMounted && isloadData === true) {
                    // Kiểm tra isMounted trước khi cập nhật state
                    data = data.map((item) => {
                        item.Date = moment(item.Date).format('DD/MM/YYYY');
                        return item;
                    });
                    data.reverse();
                    setLoadedData(false);
                    setData(data);
                }
            }
            fetchData();
        } catch (e) {
            if (axios.isCancel(e)) {
                console.log('Request canceled', e.message); // Xử lý thông báo khi request bị hủy
            } else {
                console.log(e.name, e.message);
            }
        }

        return () => {
            isMounted = false; // Đánh dấu component không còn tồn tại khi unmounted
            ourRequest.cancel();
        };
    }, [url, isloadData]);
    return {
        data,
        loadedData,
    };
};
export default useFetch;
